choose.when:
where b.writer=m.userid 
    and show='Y'
    and ${search_option} like #{keyword}

choose.otherwise:
where b.writer=m.userid 
    and show='Y'
    and (name like #{keyword} 
        or title like #{keyword}
        or content like #{keyword} )

mapper.update:
update board
set viewcnt=viewcnt+1
where bno=#{bno}

update board
set title=#{title},content=#{content}
where bno=#{bno}

mapper.delete:
update board
 set show='N' 
 where bno=#{bno}
